import * as actionTypes from "./pageTypes";

const default_state = {
  items: [
    {
      id: 1,
      title: "The first Item",
      price: 125,
      details: "This is our first item in the menu. This is very delicious....",
    },
    {
      id: 2,
      title: "The second Item",
      price: 125,
      details:
        "This is our second item in the menu. This is very very delicious....",
    },
    {
      id: 3,
      title: "The third Item",
      price: 125,
      details:
        "This is our third item in the menu. This is very very very delicious....",
    },
  ], // {id, title, price, details, img}
  cart: [], // {id, title, price, details, img, qty}
  currentItem: null,
};

const pageReducer = (state = default_state, action) => {
  switch (action.types) {
    case actionTypes.ADD_TO_CART:
      return {};
      break;
    case actionTypes.REMOVE_FROM_CART:
      return {};
      break;
    case actionTypes.UPDATE_QUTY:
      return {};
      break;
    case actionTypes.LOAD_CLICKED_ITEM:
      return {};
      break;
    default:
      return state;
      break;
  }
};
export default pageReducer;
