import React from "react";
import DeleteIcon from "@mui/icons-material/Delete";
import connect from "react-redux";
import { removeFromCart } from "../../pages/pageActions";

function CartItem({ items, deleteCartItem }) {
  return (
    <div className="card">
      <img className="item-image" src={items.image} />
      <h4>{items.title}</h4>
      <p className="details">{items.details}</p>
      <p className="price">Rs. {items.price}</p>
      <div className="cart_qty">
        <label>Quantity</label>
        <input min="1" type="number" value={items.qty} name="qty" />
      </div>
      <DeleteIcon
        onClick={() => {
          deleteCartItem(items.id);
        }}
      />
    </div>
  );
}
