import * as actionTypes from "./pageTypes";

const default_state = {
  items: [
    {
      id: 1,
      title: "Veg Thali",
      price: 80,
      details:
        "The vegetarian Kerala style lunch consists of boiled rice along with five varities of traditional dishes including Thoran, Sambar, Pullisery, Aviyal and Pickle.",
      image:
        "https://english.mathrubhumi.com/polopoly_fs/1.3342216.1580286907!/image/image.jpg_gen/derivatives/landscape_894_577/image.jpg",
    },
    {
      id: 2,
      title: "The second Item",
      price: 125,
      details:
        "This is our second item in the menu. This is very very delicious....",
      image:
        "https://4.imimg.com/data4/UU/FC/ANDROID-28469914/product-500x500.jpeg",
    },
    {
      id: 3,
      title: "The third Item",
      price: 125,
      details:
        "This is our third item in the menu. This is very very very delicious....",
    },
  ], // {id, title, price, details, img}
  cart: [], // {id, title, price, details, img, qty}
  currentItem: null,
};

const pageReducer = (state = default_state, action) => {
  switch (action.types) {
    case actionTypes.ADD_TO_CART:
      return {};
      break;
    case actionTypes.REMOVE_FROM_CART:
      return {};
      break;
    case actionTypes.UPDATE_QUTY:
      return {};
      break;
    case actionTypes.LOAD_CLICKED_ITEM:
      return {};
      break;
    default:
      return state;
      break;
  }
};
export default pageReducer;
